import React, { useState, useEffect } from 'react';
import { useAuth } from '../hooks/useAuth';
import { useNews } from '../hooks/useNews';
import { useTheme } from '../hooks/useTheme';
import NewsCard from '../components/NewsCard';
import './Profile.css';

const Profile = () => {
  const { currentUser, updateUser } = useAuth();
  const { getFavoriteNews } = useNews();
  const { theme } = useTheme();
  
  const [favoriteNews, setFavoriteNews] = useState([]);
  const [selectedThemes, setSelectedThemes] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    email: ''
  });
  const [message, setMessage] = useState({ text: '', type: '' });
  
  // Загрузка избранных новостей и предпочтений пользователя
  useEffect(() => {
    if (currentUser) {
      setFavoriteNews(getFavoriteNews(currentUser.id));
      setSelectedThemes(currentUser.preferences.themes || []);
      setFormData({
        username: currentUser.username,
        email: currentUser.email
      });
    }
  }, [currentUser, getFavoriteNews]);
  
  // Обработчик изменения полей формы
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };
  
  // Обработчик изменения выбранных тем
  const handleThemeChange = (theme) => {
    if (selectedThemes.includes(theme)) {
      setSelectedThemes(selectedThemes.filter(t => t !== theme));
    } else {
      setSelectedThemes([...selectedThemes, theme]);
    }
  };
  
  // Обработчик сохранения изменений
  const handleSaveChanges = () => {
    try {
      updateUser({
        username: formData.username,
        preferences: {
          ...currentUser.preferences,
          themes: selectedThemes
        }
      });
      
      setMessage({
        text: 'Изменения успешно сохранены',
        type: 'success'
      });
      
      setIsEditing(false);
      
      // Скрываем сообщение через 3 секунды
      setTimeout(() => {
        setMessage({ text: '', type: '' });
      }, 3000);
    } catch (error) {
      setMessage({
        text: 'Ошибка при сохранении изменений',
        type: 'error'
      });
    }
  };
  
  // Получение названия темы
  const getThemeName = (theme) => {
    switch (theme) {
      case 'politics':
        return 'Политика';
      case 'technology':
        return 'Технологии';
      case 'sports':
        return 'Спорт';
      case 'entertainment':
        return 'Развлечения';
      default:
        return 'Разное';
    }
  };
  
  // Получение иконки для темы
  const getThemeIcon = (theme) => {
    switch (theme) {
      case 'politics':
        return 'fas fa-landmark';
      case 'technology':
        return 'fas fa-microchip';
      case 'sports':
        return 'fas fa-futbol';
      case 'entertainment':
        return 'fas fa-film';
      default:
        return 'fas fa-newspaper';
    }
  };
  
  // Форматирование даты
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('ru-RU', options);
  };

  return (
    <div className={`profile-page ${theme === 'dark' ? 'dark' : 'light'}`}>
      <div className="container">
        <div className="profile-header">
          <h1>Профиль пользователя</h1>
          <button 
            className="edit-profile-btn"
            onClick={() => setIsEditing(!isEditing)}
          >
            {isEditing ? (
              <>
                <i className="fas fa-times"></i>
                <span>Отменить</span>
              </>
            ) : (
              <>
                <i className="fas fa-edit"></i>
                <span>Редактировать</span>
              </>
            )}
          </button>
        </div>
        
        {message.text && (
          <div className={`message ${message.type}`}>
            <i className={message.type === 'success' ? 'fas fa-check-circle' : 'fas fa-exclamation-circle'}></i>
            <span>{message.text}</span>
          </div>
        )}
        
        <div className="profile-content">
          <div className="profile-info">
            <div className="profile-avatar">
              <i className="fas fa-user-circle"></i>
            </div>
            
            {isEditing ? (
              <div className="profile-form">
                <div className="form-group">
                  <label htmlFor="username">Имя пользователя</label>
                  <input
                    type="text"
                    id="username"
                    name="username"
                    value={formData.username}
                    onChange={handleChange}
                    className={theme === 'dark' ? 'dark' : 'light'}
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="email">Email</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    disabled
                    className={theme === 'dark' ? 'dark' : 'light'}
                  />
                  <small>Email нельзя изменить</small>
                </div>
                
                <div className="form-group">
                  <label>Предпочитаемые темы</label>
                  <div className="theme-options">
                    {['politics', 'technology', 'sports', 'entertainment'].map(theme => (
                      <button
                        key={theme}
                        className={`theme-option ${selectedThemes.includes(theme) ? 'active' : ''}`}
                        onClick={() => handleThemeChange(theme)}
                      >
                        <i className={getThemeIcon(theme)}></i>
                        <span>{getThemeName(theme)}</span>
                      </button>
                    ))}
                  </div>
                </div>
                
                <button 
                  className="save-changes-btn"
                  onClick={handleSaveChanges}
                >
                  <i className="fas fa-save"></i>
                  <span>Сохранить изменения</span>
                </button>
              </div>
            ) : (
              <div className="profile-details">
                <div className="detail-item">
                  <span className="detail-label">Имя пользователя:</span>
                  <span className="detail-value">{currentUser.username}</span>
                </div>
                
                <div className="detail-item">
                  <span className="detail-label">Email:</span>
                  <span className="detail-value">{currentUser.email}</span>
                </div>
                
                <div className="detail-item">
                  <span className="detail-label">Дата регистрации:</span>
                  <span className="detail-value">{formatDate(currentUser.createdAt)}</span>
                </div>
                
                <div className="detail-item">
                  <span className="detail-label">Предпочитаемые темы:</span>
                  <div className="preferred-themes">
                    {selectedThemes.length > 0 ? (
                      selectedThemes.map(theme => (
                        <span key={theme} className="theme-badge">
                          <i className={getThemeIcon(theme)}></i>
                          {getThemeName(theme)}
                        </span>
                      ))
                    ) : (
                      <span className="no-themes">Не выбрано</span>
                    )}
                  </div>
                </div>
              </div>
            )}
          </div>
          
          <div className="favorite-news-section">
            <h2>Избранные новости</h2>
            
            {favoriteNews.length > 0 ? (
              <div className="favorite-news-list">
                {favoriteNews.map(news => (
                  <NewsCard key={news.id} news={news} />
                ))}
              </div>
            ) : (
              <div className="no-favorites">
                <i className="far fa-bookmark"></i>
                <p>У вас пока нет избранных новостей</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
