import React, { useEffect, useState } from 'react';
import './LoadingAnimation.css';

const LoadingAnimation = ({ onAnimationComplete }) => {
  const [isVisible, setIsVisible] = useState(true);
  const [isFading, setIsFading] = useState(false);
  const letters = 'FakeNews'.split('');

  useEffect(() => {
    // Рассчитываем общее время анимации на основе задержек анимации
    // Последняя буква (8) имеет задержку 0.8s, анимация длится 0.7s,
    // эффект свечения 1.5s после задержки 1s
    const animationDuration = 0.8 + 0.7 + 0.5; // 2 секунды
    
    const animationTimer = setTimeout(() => {
      setIsFading(true);
      
      setTimeout(() => {
        setIsVisible(false);
        if (onAnimationComplete) {
          onAnimationComplete();
        }
      }, 800); // Время для плавного исчезновения
      
    }, animationDuration * 1000);

    return () => {
      clearTimeout(animationTimer);
    };
  }, [onAnimationComplete]);

  if (!isVisible) return null;

  return (
    <div className={`loading-animation ${isFading ? 'fade-out' : ''}`}>
      <div className="letters-container">
        {letters.map((letter, index) => (
          <span
            key={index}
            className="animated-letter"
            // Задержка анимации реализована через CSS
          >
            {letter}
          </span>
        ))}
      </div>
    </div>
  );
};

export default LoadingAnimation;
