import React, { useEffect, useState } from 'react';
import './LoadingAnimation.css';

const LoadingAnimation = ({ onAnimationComplete }) => {
  const [isVisible, setIsVisible] = useState(true);
  const [isFading, setIsFading] = useState(false);
  const letters = 'FakeNews'.split('');

  useEffect(() => {
    console.log("Анимация инициализирована, буквы:", letters);
    
    const animationTimer = setTimeout(() => {
      console.log("Начинаем плавное исчезновение");
      setIsFading(true);
      setTimeout(() => {
        setIsVisible(false);
        if (onAnimationComplete) {
          console.log("Анимация завершена, вызываем колбэк");
          onAnimationComplete();
        }
      }, 600);
    }, 2400);

    return () => {
      clearTimeout(animationTimer);
    };
  }, [onAnimationComplete, letters]);

  if (!isVisible) return null;

  return (
    <div className={`loading-animation ${isFading ? 'fade-out' : ''}`}>
      <div className="letters-container">
        {letters.map((letter, index) => (
          <span
            key={index}
            className="animated-letter"
            style={{
              animationDelay: `${index * 0.15}s`
            }}
          >
            {letter}
          </span>
        ))}
      </div>
    </div>
  );
};

export default LoadingAnimation;
