import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../hooks/useAuth';
import { useNews } from '../hooks/useNews';
import { useTheme } from '../hooks/useTheme';
import './NewsCard.css';

const NewsCard = ({ news }) => {
  const { currentUser } = useAuth();
  const { likeNews, addToFavorites, removeFromFavorites, reactToNews } = useNews();
  const { theme } = useTheme();
  const [removingFavorite, setRemovingFavorite] = useState(false);
  
  // Форматирование даты
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return new Date(dateString).toLocaleDateString('ru-RU', options);
  };
  
  // Проверка, лайкнул ли пользователь новость
  const isLiked = () => {
    if (!currentUser || !news.likedBy) return false;
    return news.likedBy.includes(currentUser.uid);
  };
  
  // Проверка, добавлена ли новость в избранное
  const isFavorite = () => {
    if (!currentUser || !news.favoritedBy) return false;
    return news.favoritedBy.includes(currentUser.uid);
  };
  
  // Обработчик лайка
  const handleLike = () => {
    if (currentUser) {
      likeNews(news.id);
    } else {
      alert('Пожалуйста, войдите в систему, чтобы поставить лайк');
    }
  };
  
  // Обработчик добавления в избранное
  const handleFavorite = () => {
    if (!currentUser) {
      alert('Пожалуйста, войдите в систему, чтобы добавить в избранное');
      return;
    }

    if (isFavorite()) {
      setRemovingFavorite(true);
      setTimeout(() => {
        removeFromFavorites(news.id);
        setRemovingFavorite(false);
      }, 500);
    } else {
      addToFavorites(news.id);
    }
  };
  
  // Обработчик реакции
  const handleReaction = (reaction) => {
    if (currentUser) {
      reactToNews(news.id, reaction);
    } else {
      alert('Пожалуйста, войдите в систему, чтобы добавить реакцию');
    }
  };
  
  // Получение иконки для категории
  const getCategoryIcon = (category) => {
    switch (category.toLowerCase()) {
      case 'politics':
        return 'fas fa-landmark';
      case 'technology':
        return 'fas fa-microchip';
      case 'sports':
        return 'fas fa-futbol';
      case 'entertainment':
        return 'fas fa-film';
      default:
        return 'fas fa-newspaper';
    }
  };
  
  // Получение названия категории
  const getCategoryName = (category) => {
    switch (category.toLowerCase()) {
      case 'politics':
        return 'Политика';
      case 'technology':
        return 'Технологии';
      case 'sports':
        return 'Спорт';
      case 'entertainment':
        return 'Развлечения';
      default:
        return category;
    }
  };

  return (
    <div className={`news-card ${theme === 'dark' ? 'dark' : 'light'}`}>
      <div className="news-card-image">
        <img src={news.image} alt={news.title} />
        <div className="news-category">
          <i className={getCategoryIcon(news.category)}></i>
          <span>{getCategoryName(news.category)}</span>
        </div>
      </div>
      
      <div className="news-card-content">
        <h3 className="news-title">
          <Link to={`/news/${news.id}`}>{news.title}</Link>
        </h3>
        
        <div className="news-date">
          <i className="far fa-calendar-alt"></i>
          {formatDate(news.createdAt)}
        </div>
        
        <div className="news-excerpt">
          {news.content.length > 150 
            ? `${news.content.substring(0, 150)}...` 
            : news.content}
        </div>
        
        <div className="news-actions">
          <div className="news-reactions">
            <button 
              className={`like-btn ${isLiked() ? 'active' : ''}`} 
              onClick={handleLike}
              aria-label={isLiked() ? 'Убрать лайк' : 'Поставить лайк'}
            >
              <i className={isLiked() ? 'fas fa-heart' : 'far fa-heart'}></i>
              <span>{news.likes || 0}</span>
            </button>
            
            <div className="reaction-buttons">
              {['laugh', 'surprise', 'sad', 'angry'].map(reaction => (
                <button 
                  key={reaction}
                  className={`reaction-btn ${reaction === getUserReaction() ? 'active' : ''}`}
                  onClick={() => handleReaction(reaction)}
                  aria-label={`Реакция ${reaction}`}
                >
                  <i className={`far fa-${
                    reaction === 'laugh' ? 'laugh-beam' : 
                    reaction === 'surprise' ? 'surprise' : 
                    reaction === 'sad' ? 'sad-tear' : 
                    'angry'
                  }`}></i>
                </button>
              ))}
            </div>
          </div>
          
          <div className="news-buttons">
            <button 
              className={`favorite-btn ${isFavorite() ? 'active' : ''} ${removingFavorite ? 'remove-favorite' : ''}`} 
              onClick={handleFavorite}
              aria-label={isFavorite() ? 'Удалить из избранного' : 'Добавить в избранное'}
            >
              <i className={isFavorite() ? 'fas fa-bookmark' : 'far fa-bookmark'}></i>
              {isFavorite() ? 'В избранном' : 'В избранное'}
            </button>
            
            <Link to={`/news/${news.id}`} className="read-more-btn">
              Читать далее
              <i className="fas fa-arrow-right"></i>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewsCard;
