import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useNews } from '../hooks/useNews';
import { useAuth } from '../hooks/useAuth';
import { useTheme } from '../hooks/useTheme';
import './NewsDetail.css';

const NewsDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { getNewsById, likeNews, addReaction, toggleFavorite } = useNews();
  const { currentUser } = useAuth();
  const { theme } = useTheme();
  
  const [news, setNews] = useState(null);
  const [loading, setLoading] = useState(true);
  
  // Загрузка новости по ID
  useEffect(() => {
    const newsItem = getNewsById(id);
    if (newsItem) {
      setNews(newsItem);
    }
    setLoading(false);
  }, [id, getNewsById]);
  
  // Форматирование даты
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString('ru-RU', options);
  };
  
  // Проверка, лайкнул ли пользователь новость
  const isLiked = () => {
    return news.likedBy && news.likedBy.includes(currentUser?.id);
  };
  
  // Проверка, добавил ли пользователь реакцию
  const getUserReaction = () => {
    if (!currentUser || !news.reactedBy) return null;
    return news.reactedBy[currentUser.id];
  };
  
  // Проверка, добавлена ли новость в избранное
  const isFavorite = () => {
    if (!currentUser || !currentUser.preferences || !currentUser.preferences.favoriteNews) return false;
    return currentUser.preferences.favoriteNews.includes(news.id);
  };
  
  // Обработчик лайка
  const handleLike = () => {
    if (!currentUser) return;
    likeNews(news.id, currentUser.id);
    setNews(getNewsById(id)); // Обновляем новость после изменения
  };
  
  // Обработчик реакции
  const handleReaction = (reaction) => {
    if (!currentUser) return;
    addReaction(news.id, reaction, currentUser.id);
    setNews(getNewsById(id)); // Обновляем новость после изменения
  };
  
  // Обработчик добавления в избранное
  const handleFavorite = () => {
    if (!currentUser) return;
    toggleFavorite(news.id, currentUser.id);
    setNews(getNewsById(id)); // Обновляем новость после изменения
  };
  
  // Получение названия категории
  const getCategoryName = (category) => {
    switch (category) {
      case 'politics':
        return 'Политика';
      case 'technology':
        return 'Технологии';
      case 'sports':
        return 'Спорт';
      case 'entertainment':
        return 'Развлечения';
      default:
        return 'Разное';
    }
  };
  
  // Получение иконки для категории
  const getCategoryIcon = (category) => {
    switch (category) {
      case 'politics':
        return 'fas fa-landmark';
      case 'technology':
        return 'fas fa-microchip';
      case 'sports':
        return 'fas fa-futbol';
      case 'entertainment':
        return 'fas fa-film';
      default:
        return 'fas fa-newspaper';
    }
  };
  
  // Обработчик возврата на предыдущую страницу
  const handleGoBack = () => {
    navigate(-1);
  };

  if (loading) {
    return (
      <div className="loading-container">
        <i className="fas fa-spinner fa-spin"></i>
        <p>Загрузка новости...</p>
      </div>
    );
  }

  if (!news) {
    return (
      <div className="not-found-container">
        <i className="fas fa-exclamation-triangle"></i>
        <h2>Новость не найдена</h2>
        <p>Запрашиваемая новость не существует или была удалена.</p>
        <button className="back-btn" onClick={handleGoBack}>
          <i className="fas fa-arrow-left"></i>
          <span>Вернуться назад</span>
        </button>
      </div>
    );
  }

  return (
    <div className={`news-detail-page ${theme === 'dark' ? 'dark' : 'light'}`}>
      <div className="container">
        <button className="back-btn" onClick={handleGoBack}>
          <i className="fas fa-arrow-left"></i>
          <span>Вернуться назад</span>
        </button>
        
        <div className="news-category">
          <i className={getCategoryIcon(news.category)}></i>
          <span>{getCategoryName(news.category)}</span>
        </div>
        
        <h1 className="news-title">{news.title}</h1>
        
        <div className="news-meta">
          <span className="news-date">
            <i className="far fa-calendar-alt"></i>
            {formatDate(news.createdAt)}
          </span>
        </div>
        
        <div className="news-image">
          <img src={process.env.PUBLIC_URL + news.image} alt={news.title} />
        </div>
        
        <div className="news-content">
          {news.content.split('\n\n').map((paragraph, index) => (
            <p key={index}>{paragraph}</p>
          ))}
        </div>
        
        <div className="news-actions">
          <div className="news-reactions">
            <button 
              className={`like-btn ${isLiked() ? 'active' : ''}`} 
              onClick={handleLike}
              aria-label="Лайк"
            >
              <i className="fas fa-thumbs-up"></i>
              <span>{news.likes}</span>
            </button>
            
            <div className="reaction-buttons">
              <button 
                className={`reaction-btn ${getUserReaction() === 'laugh' ? 'active' : ''}`} 
                onClick={() => handleReaction('laugh')}
                aria-label="Смех"
              >
                <i className="fas fa-laugh"></i>
                <span>{news.reactions.laugh}</span>
              </button>
              
              <button 
                className={`reaction-btn ${getUserReaction() === 'surprise' ? 'active' : ''}`} 
                onClick={() => handleReaction('surprise')}
                aria-label="Удивление"
              >
                <i className="fas fa-surprise"></i>
                <span>{news.reactions.surprise}</span>
              </button>
              
              <button 
                className={`reaction-btn ${getUserReaction() === 'sad' ? 'active' : ''}`} 
                onClick={() => handleReaction('sad')}
                aria-label="Грусть"
              >
                <i className="fas fa-sad-tear"></i>
                <span>{news.reactions.sad}</span>
              </button>
              
              <button 
                className={`reaction-btn ${getUserReaction() === 'angry' ? 'active' : ''}`} 
                onClick={() => handleReaction('angry')}
                aria-label="Злость"
              >
                <i className="fas fa-angry"></i>
                <span>{news.reactions.angry}</span>
              </button>
            </div>
          </div>
          
          <div className="news-buttons">
            <button 
              className={`favorite-btn ${isFavorite() ? 'active' : ''}`} 
              onClick={handleFavorite}
              aria-label={isFavorite() ? 'Удалить из избранного' : 'Добавить в избранное'}
            >
              <i className={isFavorite() ? 'fas fa-bookmark' : 'far fa-bookmark'}></i>
              <span>{isFavorite() ? 'В избранном' : 'В избранное'}</span>
            </button>
          </div>
        </div>
        
        <div className="share-section">
          <h3>Поделиться новостью</h3>
          <div className="share-buttons">
            <a 
              href={`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(window.location.href)}&quote=${encodeURIComponent(news.title)}`} 
              target="_blank" 
              rel="noopener noreferrer"
              className="share-btn facebook"
            >
              <i className="fab fa-facebook-f"></i>
              <span>Facebook</span>
            </a>
            
            <a 
              href={`https://twitter.com/intent/tweet?text=${encodeURIComponent(news.title)}&url=${encodeURIComponent(window.location.href)}`} 
              target="_blank" 
              rel="noopener noreferrer"
              className="share-btn twitter"
            >
              <i className="fab fa-twitter"></i>
              <span>Twitter</span>
            </a>
            
            <a 
              href={`https://vk.com/share.php?url=${encodeURIComponent(window.location.href)}&title=${encodeURIComponent(news.title)}`} 
              target="_blank" 
              rel="noopener noreferrer"
              className="share-btn vk"
            >
              <i className="fab fa-vk"></i>
              <span>VK</span>
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewsDetail;
